    @Test
    fun `parse valid json`() {
        every { base64Wrapper.decode(any()) } returns "decoded adm"
        val adUnit = adUnitParser.parse(json)
        assertNotNull(adUnit)
        assertEquals("", adUnit.name)
        assertEquals("Rn6mAJ5PpyrUdROxUq5/Ng==", adUnit.adId)
        assertEquals("9a6dc32c90c3659e0eb09e9834392f6f86be147e", adUnit.impressionId)
        assertEquals("TmORoOg/mXDIXNWVmN7Fi9d3E5WrEjg", adUnit.cgn)
        assertEquals(
            "{\"frame\":\"default\",\"close_button\":\"default\",\"is_mraid_playable\":false,\"creative_id\":\"CR50780105\"}",
            adUnit.creative,
        )
        assertEquals("video", adUnit.mediaType)
        assertEquals(1, adUnit.assets.size)
        assertEquals("", adUnit.videoUrl)
        assertEquals("", adUnit.videoFilename)
        assertEquals("itunes", adUnit.link)
        assertEquals("", adUnit.deepLink)
        assertEquals("5fd27b0d679ad8081bcbef20", adUnit.to)
        assertEquals(0, adUnit.rewardAmount)
        assertEquals("", adUnit.rewardCurrency)
        assertEquals("6319dccb582ae7076518965c", adUnit.template)
        assertEquals("https://t2.chartboost.com/base_templates/html/vast-moat-v2-8c431e484e.html", adUnit.body.url)
        assertEquals(21, adUnit.parameters.size)
        assertEquals(1, adUnit.events.size)
        assertEquals("decoded adm", adUnit.decodedAdm)
    }

    @Test(expected = JSONException::class)
    fun `parse invalid data missing adid return empty adunit`() {
        val adUnit = adUnitParser.parse(jsonInvalid)
        assertNull(adUnit)
    }

    @Test(expected = JSONException::class)
    fun `parse null returns empty ad unit`() {
        val adunit = adUnitParser.parse(null)
        assertNull(adunit)
    }
